let status = flow.get("mqtt_status") || {};


if (status.text == 'node-red:common.status.connected') {
    msg.red = 'online';
    msg.cabecera.estado_enviado = true;
    msg.detalle.estado_enviado = true;
    msg.movil.estado_enviado = true;
    msg.fecha = new Date();
} else {
    msg.red = 'offline';
    msg.cabecera.estado_enviado = false;
    msg.detalle.estado_enviado = false;
    msg.movil.estado_enviado = false;
    msg.fecha = new Date();
}
return msg;

if (!msg.payload) {
    return [null, msg];
}

if (msg.payload.length < 1) {
    return [null, msg];
}

//All checks have passed (data is ok!)
return [msg, null];

var newMsg = [];

/**
* @param {Date} timestamp
* @returns {{fecha: string, hora: string}} - Objeto con propiedades 'fecha' y 'hora'.
*/
function formatDateTime(timestamp) {

    if (!timestamp) {
        // Manejar el caso cuando timestamp es undefined o null
        return {
            fecha: '',
            hora: ''
        };
    }

    var formattedDate = timestamp.toLocaleDateString('es-ES', { day: '2-digit', month: '2-digit', year: 'numeric' });
    var formattedTime = timestamp.toLocaleTimeString('es-ES', { hour: '2-digit', minute: '2-digit' });
    return {
        fecha: formattedDate,
        hora: formattedTime
    };
}

var arr = msg.payload.result;
var serial = msg.payload.serial;
var model = msg.payload.model;

var newMsg = []; // Arreglo donde almacenaremos los datos a enviar

for (var i = 0; i < arr.length; i++) {
    var currentItem = arr[i];
    if (currentItem.id !== undefined && currentItem.id !== null) {
        var timestamp = new Date(currentItem.timestamp);
        var formattedDateTime = formatDateTime(timestamp);

        var nombreModuloZona;

        if (currentItem.zones[0] === 'abf76d76-ca23-4375-442f-1acf321ccb10') {
            nombreModuloZona = '100';
        } else if (currentItem.zones[0] === '375b49b7-4590-4179-7636-5d04ad56541e') {
            nombreModuloZona = 'INTERIOR';
        } else if (currentItem.zones[0] === '30e955bf-3dad-482b-63c3-1a78e1f6ca99') {
            nombreModuloZona = 'EXTERIOR';
        } else if (currentItem.zones[0] === 'ab3ee495-3bb7-45f7-5fcd-c09bdf7dfca1') {
            nombreModuloZona = 'ACTIVE_TRACKING';
        }else {
            nombreModuloZona = currentItem.zones[0];
        }
        // Agregar una condicin para omitir cuando nombreModuloZona es 'ACTIVE_TRACKING'
        if (nombreModuloZona !== 'ACTIVE_TRACKING') {
            var cabecera = {
                track_id: currentItem.id,
                seccion: 'seguridad',
                codigo_empresa: msg.codigo_empresa,
                empresa: msg.empresa,
                codigo_centro: msg.codigo_centro,
                centro: msg.centro,
                clasificacion: currentItem.classification?.type?.value || '',
                fecha: formattedDateTime.fecha,
                hora: formattedDateTime.hora,
                model: model || '',
                serial: serial || '',
            };
            // Agregar "id_modulo" solo cuando es un mdulo
            if (nombreModuloZona === '100') {
                cabecera.id_modulo = currentItem.zones[0];
            } else {
                // Agregar "id_zona" solo cuando es una zona
                cabecera.id_zona = currentItem.zones[0];
            }
            // Agregar "nombre_modulo" solo cuando es un mdulo
            if (nombreModuloZona === '100') {
                cabecera.modulo = nombreModuloZona;
            }

            // Agregar "nombre_zona" solo cuando es una zona
            if (nombreModuloZona === 'INTERIOR' || nombreModuloZona === 'EXTERIOR' || nombreModuloZona === 'ACTIVE_TRACKING') {
                cabecera.zona = nombreModuloZona;
            }
            cabecera.id_usuario = null ;
            cabecera.comentario = '';
            cabecera.observacion = '';
            cabecera.confirmado = false;
            cabecera.estado_enviado = false;

            var detalle = {
                track_id: currentItem.id,
                seccion: 'seguridad_detalle',
                codigo_empresa: msg.codigo_empresa,
                empresa: msg.empresa,
                centro: msg.centro,
                codigo_centro: msg.codigo_centro,
                fecha: formattedDateTime.fecha,
                hora: formattedDateTime.hora,
                clasificacion: currentItem.classification?.type?.value || '',
                latitud: currentItem.geolocation.latitude,
                longitud: currentItem.geolocation.longitude,
                heading: currentItem.geolocation.heading,
                velocidad: currentItem.geolocation.speed,
                distancia: currentItem.totalDistance,
                duracion: currentItem.duration + "s",
                desplazamiento: currentItem.displacement,
                aceleracion: currentItem.acceleration,
                snr: currentItem.stats?.localSnr || '',
            };

            // Agregar "nombre_modulo" solo cuando es un mdulo
            if (nombreModuloZona === '100') {
                detalle.modulo = nombreModuloZona;
            }

            // Agregar "nombre_zona" solo cuando es una zona
            if (nombreModuloZona === 'INTERIOR' || nombreModuloZona === 'EXTERIOR' || nombreModuloZona === 'ACTIVE_TRACKING') {
                detalle.zona = nombreModuloZona;
            }
            detalle.estado_enviado = false;


            var movil = {
                track_id: currentItem.id,
                seccion: 'seguridad',
                codigo_empresa: msg.codigo_empresa,
                empresa: msg.empresa,
                codigo_centro: msg.codigo_centro,
                centro: msg.centro,
                clasificacion: currentItem.classification?.type?.value || '',
                fecha: formattedDateTime.fecha,
                hora: formattedDateTime.hora,
                latitud: currentItem.geolocation.latitude,
                longitud: currentItem.geolocation.longitude,

            };
            // Agregar "nombre_modulo" solo cuando es un mdulo
            if (nombreModuloZona === '100') {
                movil.modulo = nombreModuloZona;
            }

            // Agregar "nombre_zona" solo cuando es una zona
            if (nombreModuloZona === 'INTERIOR' || nombreModuloZona === 'EXTERIOR' || nombreModuloZona === 'ACTIVE_TRACKING') {
                movil.zona = nombreModuloZona;
            }

            movil.estado_enviado = false;

            newMsg.push({
                search: currentItem.id,
                cabecera: cabecera,
                detalle: detalle,
                movil: movil,
            });
        }    
    }
}

return [newMsg];

